urlset:
{apiget type="section" var="sections"}
{foreach from=$sections item="section"}
  {foreach from=$section->getItems() item="item"}
    {if !$item->isArchived()}
      {wrap var="wrapped" item=$item->getCurrentVersion($session.variant)}
      {if ($wrapped !== null)}

          http://{$SERVER.HTTP_HOST}{$wrapped->url|escape}
          {$wrapped->published|date_format:"%Y-%m-%dT%H:%M:%S+00:00"|default:"2006-01-01T00:00:00+00:00"}

      {/if}
    {/if}
  {/foreach}
{/foreach}
